'use strict';

var fileTypes = ['othe', 'zip', 'avi', 'swf', 'wav', 'txt', 'rmvb', 'xls', 'mkv', 'psd', 'exe', 'gif', 'pdf', 'mp4', 'ppt', 'png', 'mp3', 'jpg', 'doc'];
var layer = null;
$(function () {
    var table = layui.table;
    var options = {
        url: '/manage/getClassList',
        elem: '#demo',
        id: 'demo',
        page: {
            curr: 1 //重新从第 1 页开始
        },
        limit: 10,
        limits: [10, 15, 20],
        height: 'full-200',
        where: {},
        cols: [[{
            field: 'id',
            title: 'ID',
            width: 80
        }, {
            field: 'classname',
            title: '中文标题'
        }, {
            fixed: 'right',
            align: 'center',
            title: '操作',
            width: 180,
            field: '',
            templet: function templet(d) {
                return '<a class="layui-btn layui-btn-primary layui-btn-xs" lay-event="edit">\u7F16\u8F91</a>\n                    <a class="layui-btn layui-btn-danger layui-btn-xs" lay-event="del">\u5220\u9664</a>';
            }
        }]]
    };
    var DownloadList = table.render(options);

    table.on('tool(demo)', function (obj) {
        //注：tool是工具条事件名，test是table原始容器的属性 lay-filter="对应的值"
        var $data = obj.data; //获得当前行数据
        var layEvent = obj.event; //获得 lay-event 对应的值（也可以是表头的 event 参数对应的值）
        var tr = obj.tr; //获得当前行 tr 的DOM对象
        if (layEvent === 'del') {
            //删除
            layer.confirm('真的要删除这个分类吗？', function (index) {
                // obj.del(); //删除对应行（tr）的DOM结构，并更新缓存
                layer.close(index);
                $.ajax({
                    type: "POST",
                    url: "/manage/deleteclass",
                    dataType: "json",
                    data: {
                        id: $data.id
                    },
                    success: function success(r) {
                        if (r.ok === 200) {
                            obj.del();
                        } else {
                            layer.msg(r.msg, function () {});
                        }
                    }
                });
            });
        } else {
            layer.open({
                type: 1,
                area: '300px',
                title: '修改分类名称',
                content: '<div class="addfile-container">\n                <form class="layui-form layui-form-pane">\n                    <div class="layui-form-item">\n                        <label class="layui-form-label">\u5206\u7C7B\u540D\u79F0</label>\n                        <div class="layui-input-block">\n                            <input type="text" name="title" lay-verify="" placeholder="\u8BF7\u8F93\u5165\u5206\u7C7B\u540D\u79F0" autocomplete="off" class="layui-input" value="' + $data.classname + '">\n                        </div>\n                    </div>\n                    \n                    <div class="layui-form-item btn-box">\n                        <button class="layui-btn" lay-submit lay-filter="submit" id="btn-submit">\u7ACB\u5373\u63D0\u4EA4</button>\n                    </div>\n                </form>\n            </div>',
                success: function success(el, index) {
                    console.log(el, index);
                    layui.use('form', function () {
                        var form = layui.form;
                        form.on('submit(submit)', function (data) {
                            console.log(data);
                            if (!$.trim(data.field.title)) {
                                layer.msg('分类标题不能为空', function () {});
                                return false;
                            }

                            var post = {
                                id: $data.id,
                                classname: $.trim(data.field.title)
                            };

                            console.log(post);

                            $.ajax({
                                type: "POST",
                                url: "/manage/changeclass",
                                dataType: "json",
                                data: post,
                                success: function success(data) {
                                    if (data.ok === 200) {
                                        // targetParent('DownloadList')
                                        location.reload();
                                    } else {
                                        layer.msg(data.msg);
                                    }
                                }
                            });
                            return false;
                        });
                    });
                }
            });
        }
    });

    layui.use('layer', function () {
        layer = layui.layer;
        $('#btn-addfile').on('click', function () {
            var index = layer.open({
                type: 1,
                area: '300px',
                content: '<div class="addfile-container">\n                <form class="layui-form layui-form-pane">\n                    <div class="layui-form-item">\n                        <label class="layui-form-label">\u5206\u7C7B\u540D\u79F0</label>\n                        <div class="layui-input-block">\n                            <input type="text" name="title" lay-verify="" placeholder="\u8BF7\u8F93\u5165\u5206\u7C7B\u540D\u79F0" autocomplete="off" class="layui-input">\n                        </div>\n                    </div>\n                    \n                    <div class="layui-form-item btn-box">\n                        <button class="layui-btn" lay-submit lay-filter="submit" id="btn-submit">\u7ACB\u5373\u63D0\u4EA4</button>\n                    </div>\n                </form>\n            </div>',
                success: function success(el, index) {
                    console.log(el, index);
                    layui.use('form', function () {
                        var form = layui.form;
                        form.on('submit(submit)', function (data) {
                            console.log(data);
                            if (!$.trim(data.field.title)) {
                                layer.msg('分类标题不能为空', function () {});
                                return false;
                            }

                            var post = {
                                classname: $.trim(data.field.title)
                            };

                            $.ajax({
                                type: "POST",
                                url: "/manage/changeclass",
                                dataType: "json",
                                data: post,
                                success: function success(data) {
                                    if (data.ok === 200) {
                                        // targetParent('DownloadList')
                                        location.reload();
                                    } else {
                                        layer.msg(data.msg);
                                    }
                                }
                            });

                            return false;
                        });
                    });
                }
            });
        });
    });
});

//获取后缀名
function getFileType(name) {
    var dotindex = name.lastIndexOf('.');
    return name.substring(dotindex + 1, name.length);
}

//转换文件大小
function bytesToSize(bytes) {
    if (bytes === 0) return '0 B';
    var k = 1024,
        // or 1024
    sizes = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'],
        i = Math.floor(Math.log(bytes) / Math.log(k));

    return (bytes / Math.pow(k, i)).toPrecision(3) + sizes[i];
}